mapper.select:
SELECT DISTINCT B.ID, B.APPLY_DATE, B.APPLY_NAME, B.BUSINESS_ID, B.CATEGORY_ID, B.CREATE_DATE, B.CREATE_NAME, B. LEVEL, B. NAME, B.PROJECT_NO, B. STATUS, B.USER_NAME, B.WORKFLOW_ID, B.WORKFLOW_NAME FROM wf_base b, act_hi_procinst p, act_hi_taskinst t WHERE b.ID = p.BUSINESS_KEY_ AND p.PROC_INST_ID_ = t.PROC_INST_ID_ AND t.ASSIGNEE_ = #{userName}
and b.CATEGORY_ID = #{categoryId}
and b.PROJECT_NO = #{projectNo}
and b.NAME like concat('%',#{name},'%')
and b.APPLY_NAME = #{applyName}
and b.APPLY_DATE >= #{applyDateStart}
and b.APPLY_DATE <= #{applyDateEnd}
order by b.APPLY_DATE desc

SELECT DISTINCT B.ID, B.APPLY_DATE, B.APPLY_NAME, B.BUSINESS_ID, B.CATEGORY_ID, B.CREATE_DATE, B.CREATE_NAME, B. LEVEL, B. NAME, B.PROJECT_NO, B. STATUS, B.USER_NAME, B.WORKFLOW_ID, B.WORKFLOW_NAME FROM act_ru_identitylink a LEFT JOIN act_ru_task t ON a.TASK_ID_ = t.ID_ LEFT JOIN act_ru_execution e ON t.PROC_INST_ID_ = e.PROC_INST_ID_ LEFT JOIN wf_base B ON E.BUSINESS_KEY_ = B.ID WHERE a.USER_ID_ = #{userName} AND a.type_ = 'candidate' AND e.BUSINESS_KEY_ IS NOT NULL
and b.CATEGORY_ID = #{categoryId}
and b.PROJECT_NO = #{projectNo}
and b.NAME like concat('%',#{name},'%')
and b.APPLY_NAME = #{applyName}
and b.APPLY_DATE >= #{applyDateStart}
and b.APPLY_DATE <= #{applyDateEnd}
ORDER BY B.APPLY_DATE DESC

SELECT DISTINCT B.ID, B.APPLY_DATE, B.APPLY_NAME, B.BUSINESS_ID, B.CATEGORY_ID, B.CREATE_DATE, B.CREATE_NAME, B. LEVEL, B. NAME, B.PROJECT_NO, B. STATUS, B.USER_NAME, B.WORKFLOW_ID, B.WORKFLOW_NAME FROM wf_base b, act_ru_execution e, act_ru_task t WHERE b.ID = e.BUSINESS_KEY_ AND e.PROC_INST_ID_ = t.PROC_INST_ID_ AND t.ASSIGNEE_ = #{userName}
and b.CATEGORY_ID = #{categoryId}
and b.PROJECT_NO = #{projectNo}
and b.NAME like concat('%',#{name},'%')
and b.APPLY_NAME = #{applyName}
and b.APPLY_DATE >= #{applyDateStart}
and b.APPLY_DATE <= #{applyDateEnd}
order by b.APPLY_DATE desc
